$(document).ready(function(){facebook_sharurl=$("#sharFB");twitter_sharurl=$("#sharTW");linkedin_sharurl=$("#sharLI");bufferapp_sharurl=$("#sharBA");_callback_sharurl="&callback=?";var e,t;$.getJSON("https://api.facebook.com/method/fql.query?query=select%20total_count%20from%20link_stat%20where%20url='"+_sharurl+"'&format=json",function(t){return e($(facebook_sharurl),t[0].total_count)});$.getJSON("http://urls.api.twitter.com/1/urls/count.json?url="+_sharurl+_callback_sharurl,function(t){return e($(twitter_sharurl),t.count)});$.getJSON("http://www.linkedin.com/countserv/count/share?url="+_sharurl+_callback_sharurl,function(t){return e($(linkedin_sharurl),t.count)});$.getJSON("https://api.bufferapp.com/1/links/shares.json?url="+_sharurl+_callback_sharurl,function(t){return e($(bufferapp_sharurl),t.shares)});t=function(e){return setTimeout(function(){var n,r,i;n=e.attr("sharurl")*1;i=e.attr("data-sharurl")*1;r=n+Math.ceil((i-n)/3);e.attr("sharurl",r);e.html(r);if(r<i){return t(e)}},100)};e=function(e,n){if(n==null){n=null}e.attr("data-sharurl",n);e.attr("sharurl",0);return t(e)}})
